import React from 'react';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import {navStyle} from "../navStyle";
const phones = require('../imgs/phones.png');
import '../css/jumbotron.css';


class JumboTron extends React.Component {
   render () {
     let { color } = navStyle;

     return (
       <Jumbotron style={{backgroundColor: '#F2F2F2'}}>
         <Row className="justify-content-md-center">
           <Col lg='5'>
             <img src={phones} style={{width: '100%'}}/>
           </Col>
           <Col lg='5' className="align-self-center" style={{alignContent: 'space-between'}}>
             <h1>Track and Manage Your Money</h1>
             <p style={{width: '70%', marginTop: '2em', marginBottom: '2em'}}>
             Get the most advanced technology in personal finance, now available to everyone.
             </p>
             <p>
               <Button style={{border: '2px solid #FD9D2E', color: color, backgroundColor: '#FD9D2E'}}>Sign Up for Free Tools</Button>
             </p>
           </Col>
         </Row>
       </Jumbotron>
     )
   }
}

export default JumboTron;
