import React from 'react';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import {navStyle} from "../navStyle";
const phones = require('../imgs/phones.png');

class JumboTron extends React.Component {
   render () {
     let { color, backgroundColor, brand, justifyContent, marginLeft, marginRight } = navStyle;

     return (
       <Jumbotron>
         <Row>
           <Col md='auto'>
             <img src={phones} style={{width: '80%'}}/>
           </Col>
           <Col>
             <h1>Track and Manage Your Money</h1>
             <p>
             Get the most advanced technology in personal finance, now available to everyone.
             </p>
             <p>
               <Button style={{marginLeft: marginLeft, marginRight: marginRight, border: this.state.border, backgroundColor: '#FD9D2E'}}>Sign Up</Button>
             </p>
           </Col>
         </Row>
       </Jumbotron>
     )
   }
}

export default JumboTron;
